def transform_json(input_data):
    """
    Transforms the given data by applying specific rules to each item.

    :param input_data: Either a JSON formatted string or a list of items to be transformed.
    :return: A JSON formatted string with the transformed items.
    """

    def _transform_item(item):
        """Transform a single JSON item according to the specified rules."""
        if 'raw_label' in item and item['raw_label']:
            try:
                # Assuming raw_label is a string representation of a list, parse it using json.loads
                item['labels'] = json.loads(item['raw_label'])
            except json.JSONDecodeError:
                # If parsing fails, attempt to replace single quotes with double quotes and try again
                cleaned_raw_label = item['raw_label'].replace("'", '"')
                item['labels'] = json.loads(cleaned_raw_label)

        # Remove the raw_label field
        item.pop('raw_label', None)
        return item

    # 如果输入是字符串，则先解析为Python对象
    if isinstance(input_data, str):
        input_data = json.loads(input_data)
    elif not isinstance(input_data, list):
        raise TypeError("Input must be a JSON string or a list.")

    # 对每个条目应用转换
    output_data = [_transform_item(item) for item in input_data]

    # 返回转换后的数据，以JSON格式字符串形式
    return json.dumps(output_data, ensure_ascii=False)


def process_and_trim_top_k(input_json_str):
    """
    处理给定的JSON字符串中的数据，修剪每个CVE条目的top_k列表，保留前10个lib_name。
    如果不足10个，补空对象直到有10个。

    :param input_json_str: 输入的JSON格式字符串。
    :return: 处理后的JSON格式字符串。
    """

    def trim_top_k_to_ten(entries):
        """对每个CVE条目的top_k列表进行处理，确保每个CVE条目都有10个lib_name"""
        for entry in entries:
            top_k = entry.get('top_k', [])
            # 清除空对象或多余的逗号导致的无效项
            top_k = [item for item in top_k if isinstance(item, dict) and 'lib_name' in item]
            # 保证每个CVE条目的top_k列表至少有10个元素
            while len(top_k) < 10:
                top_k.append({"lib_name": "", "website_description": ""})
            entry['top_k'] = top_k[:10]  # 确保不超过10个有效的库名称
        return entries

    # 将输入的JSON字符串解析为Python对象
    try:
        input_data = json.loads(input_json_str)
    except json.JSONDecodeError as e:
        raise ValueError(f"Invalid JSON input: {e}")

    # 检查输入是否为列表
    if not isinstance(input_data, list):
        raise TypeError("Input must be a JSON string representing a list of items.")

    # 处理数据：修剪每个CVE条目的top_k列表
    trimmed_entries = trim_top_k_to_ten(input_data)

    # 返回处理后的数据，以JSON格式字符串形式
    return json.dumps(trimmed_entries, ensure_ascii=False, indent=2)



def clean_package(package):
    # 清除包名前后可能存在的多余空白和句号，并移除maven:前缀
    package = package.strip().rstrip('.').strip('"')
    if package.startswith("maven:"):
        package = package[len("maven:"):]
    return package


def extract_affected_package_from_instruction(instruction_response_str):
    """
    从给定的包含Instruction和Response的字符串中提取受影响的包。

    参数:
    instruction_response_str (str): 包含Instruction和Response的字符串。

    返回:
    str: 受影响的库名，格式为group id:artifact id。
    如果没有找到，则返回空字符串。
    """
    lines = instruction_response_str.strip().split('\n')
    for line in lines:
        if "The affected package is" in line:
            # 查找maven:的位置
            start_index = line.find("maven:")
            if start_index != -1:
                package_str = line[start_index:].replace("</s>", "").strip()
                # 提取并清理包名
                package = clean_package(package_str)
                print(f"Extracted package: {package}")  # 调试信息
                return package
    return ""


