def extract_affected_package_from_c_instruction(instruction_response_str):
    """
    从给定的包含Instruction和Response的字符串中提取受影响的C语言包。

    参数:
    instruction_response_str (str): 包含Instruction和Response的字符串。

    返回:
    list: 受影响的库名列表，格式依据实际情况。
    如果没有找到，则返回空列表。
    """
    lines = instruction_response_str.strip().split('\n')
    affected_packages = []
    for line in lines:
        if "The affected package is" in line:
            # 提取"The affected package is"之后的文本并清理
            start_index = line.find("The affected package is") + len("The affected package is")
            package_str = line[start_index:].replace("</s>", "").strip()
            # 避免误将句子的其余部分作为包名
            if not package_str or package_str.startswith("."):
                continue
            package = clean_package_c(package_str)
            if package:
                print(f"Extracted package: {package}")  # 调试信息
                affected_packages.append(package)
    return affected_packages

def extract_top_libraries(afterNormalizationText, similarityThreshold):
    try:
        # 将 JSON 字符串转换为 Python 对象
        data = json.loads(afterNormalizationText)

        # 检查数据结构是否符合预期
        if not isinstance(data, list) or len(data) == 0 or 'rerank_k' not in data[0]:
            return ''

        # 获取 rerank_k 列表
        rerank_k = data[0]['rerank_k']

        # 筛选出满足相似度阈值的库
        filtered_libraries = [item for item in rerank_k if item['re_rank_score'] >= similarityThreshold]

        # 根据 re_rank_score 排序并提取前三个
        sorted_libraries = sorted(filtered_libraries, key=lambda x: x['re_rank_score'], reverse=True)[:3]

        # 提取 lib_name
        top_three_libraries = [item['lib_name'] for item in sorted_libraries]

        # 如果找到的库少于3个，则用空字符串填充
        while len(top_three_libraries) < 3:
            top_three_libraries.append("")

        # 使用分号连接这些库名
        return ';'.join(top_three_libraries)

    except json.JSONDecodeError as e:
        print(f"JSON decode error: {e}")
        return ''
    except Exception as e:
        print(f"An error occurred: {e}")
        return ''
